mapper.select:
		
   			SELECT IFNULL(MAX(BOARD_NUM), 0)+1 AS NUM 
			FROM BOARD_REPORT 
			WHERE BOARD_REPORT_TYPE = #{boardReportType};

SELECT TM.* FROM (SELECT TP.REPORT_ID, TP.MEMBER_ID, TP.SIDO, TP.SIGUNGU, TP.BNAME, TP.ADDRESS, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'KIND' AND TYPE_D = TP.PET_KIND) AS PET_KIND, (SELECT STR FROM TYPE_STR WHERE TYPE_D = TP.PET_DETAIL_KIND) AS PET_DETAIL_KIND, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'GENDER' AND TYPE_D = TP.PET_GENDER) AS PET_GENDER, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'SIZE' AND TYPE_D = TP.PET_SIZE) AS PET_SIZE, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'COLOR' AND TYPE_D = TP.PET_COLOR) AS PET_COLOR, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'COATlENGTH' AND TYPE_D = TP.PET_COATlENGTH) AS PET_COATlENGTH, TP.PET_CHARACTER, DATE_FORMAT(TP.PET_FIND_DATE, '%Y-%m-%d') AS PET_FIND_DATE, DATE_FORMAT(TP.PET_FIND_DATE, '%Y-%m-%d') AS PET_LOST_DATE, TB.BOARD_NUM, TB.BOARD_REPORT_TYPE, TB.MEMBER_NICKNAME, TB.BOARD_REPORT_TITLE, TB.BOARD_REPORT_READ_COUNT, TB.BOARD_REPORT_REPT_COUNT, TB.BOARD_REPORT_GOOD, TB.BOARD_REPORT_NOT_GOOD, TB.BOARD_REPORT_REG_DATE, TA.ATTACH_UUID, TA.ATTACH_UPLOAD_PATH, TA.ATTACH_NAME, TA.ATTACH_TYPE FROM BOARD_REPORT TB LEFT OUTER JOIN PET_FIND_REPORT TP ON TB.REPORT_ID = TP.REPORT_ID LEFT OUTER JOIN ATTACH_REPORT TA ON TB.REPORT_ID = TA.REPORT_ID WHERE TB.BOARD_REPORT_TYPE = 'F'
AND PET_KIND = #{petKind}
AND PET_DETAIL_KIND = #{petDetailKind}
AND PET_GENDER = #{petGender}
AND PET_SIZE = #{petSize}
AND PET_COLOR = #{petColor}
AND PET_COATlENGTH = #{petCoatLength}
AND SIDO = #{sido}
AND SIGUNGU = #{sigungu}
AND BNAME = #{bname}
AND DATE(PET_FIND_DATE) BETWEEN #{startDate} AND #{endDate}
AND DATE(PET_FIND_DATE) = #{startDate}
UNION ALL
SELECT TP.REPORT_ID, TP.MEMBER_ID, TP.SIDO, TP.SIGUNGU, TP.BNAME, TP.ADDRESS, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'KIND' AND TYPE_D = TP.PET_KIND) AS PET_KIND, (SELECT STR FROM TYPE_STR WHERE TYPE_D = TP.PET_DETAIL_KIND) AS PET_DETAIL_KIND, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'GENDER' AND TYPE_D = TP.PET_GENDER) AS PET_GENDER, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'SIZE' AND TYPE_D = TP.PET_SIZE) AS PET_SIZE, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'COLOR' AND TYPE_D = TP.PET_COLOR) AS PET_COLOR, (SELECT STR FROM TYPE_STR WHERE TYPE_M = 'COATlENGTH' AND TYPE_D = TP.PET_COATlENGTH) AS PET_COATlENGTH, TP.PET_CHARACTER, DATE_FORMAT(TP.PET_LOST_DATE, '%Y-%m-%d') AS PET_FIND_DATE, DATE_FORMAT(TP.PET_LOST_DATE, '%Y-%m-%d') AS PET_LOST_DATE, TB.BOARD_NUM, TB.BOARD_REPORT_TYPE, TB.MEMBER_NICKNAME, TB.BOARD_REPORT_TITLE, TB.BOARD_REPORT_READ_COUNT, TB.BOARD_REPORT_REPT_COUNT, TB.BOARD_REPORT_GOOD, TB.BOARD_REPORT_NOT_GOOD, TB.BOARD_REPORT_REG_DATE, TA.ATTACH_UUID, TA.ATTACH_UPLOAD_PATH, TA.ATTACH_NAME, TA.ATTACH_TYPE FROM BOARD_REPORT TB LEFT OUTER JOIN PET_LOST_REPORT TP ON TB.REPORT_ID = TP.REPORT_ID LEFT OUTER JOIN ATTACH_REPORT TA ON TB.REPORT_ID = TA.REPORT_ID WHERE TB.BOARD_REPORT_TYPE = 'L'
AND PET_KIND = #{petKind}
AND PET_DETAIL_KIND = #{petDetailKind}
AND PET_GENDER = #{petGender}
AND PET_SIZE = #{petSize}
AND PET_COLOR = #{petColor}
AND PET_COATlENGTH = #{petCoatLength}
AND SIDO = #{sido}
AND SIGUNGU = #{sigungu}
AND BNAME = #{bname}
AND DATE(PET_LOST_DATE) BETWEEN #{startDate} AND #{endDate}
AND DATE(PET_LOST_DATE) = #{startDate}
) TM WHERE 1=1
AND TM.BOARD_REPORT_TYPE = #{inlineRadioOptions}
GROUP BY REPORT_ID ORDER BY PET_FIND_DATE DESC, PET_LOST_DATE DESC LIMIT #{startRow}, #{amount}

SELECT COUNT(*) FROM (SELECT SIDO, SIGUNGU, BNAME, ADDRESS, PET_KIND, PET_DETAIL_KIND, PET_GENDER, PET_SIZE, PET_COLOR, PET_COATlENGTH, PET_CHARACTER, DATE_FORMAT(PET_FIND_DATE, '%Y-%m-%d') AS PET_FIND_DATE, DATE_FORMAT(PET_FIND_DATE, '%Y-%m-%d') AS PET_LOST_DATE FROM PET_FIND_REPORT
UNION ALL
SELECT
SIDO, SIGUNGU, BNAME, ADDRESS, PET_KIND, PET_DETAIL_KIND, PET_GENDER, PET_SIZE, PET_COLOR, PET_COATlENGTH, PET_CHARACTER, DATE_FORMAT(PET_LOST_DATE, '%Y-%m-%d') AS PET_FIND_DATE, DATE_FORMAT(PET_LOST_DATE, '%Y-%m-%d') AS PET_LOST_DATE FROM PET_LOST_REPORT) TM LEFT OUTER JOIN BOARD_REPORT TR ON TM.REPORT_ID = TR.REPORT_ID
AND PET_KIND = #{petKind}
AND PET_DETAIL_KIND LIKE #{petDetailKind}
AND PET_GENDER = #{petGender}
AND PET_SIZE = #{petSize}
AND PET_COLOR = #{petColor}
AND PET_COATlENGTH = #{petCoatLength}
AND SIDO = #{sido}
AND SIGUNGU = #{sigungu}
AND BNAME = #{bname}
AND DATE(PET_LOST_DATE) BETWEEN #{startDate} AND #{endDate}
AND DATE(PET_FIND_DATE) BETWEEN #{startDate} AND #{endDate}
AND DATE(PET_LOST_DATE) = #{startDate}
AND DATE(PET_LOST_DATE) = #{startDate}
AND BOARD_REPORT_TYPE = #{inlineRadioOptions}

mapper.update:

	UPDATE BOARD_REPORT 
	SET BOARD_REPORT_TITLE = #{boardTitle}
	WHERE REPORT_ID = #{reportId}


	UPDATE BOARD_REPORT 
	SET BOARD_REPORT_READ_COUNT = BOARD_REPORT_READ_COUNT + 1
	WHERE REPORT_ID = #{reportId}
