mapper.select:

   	 SELECT COUNT(GOOD_OR_NOT)
	 FROM BOARD_COM_CONFIG
	 WHERE 
	 	BOARD_ID 		= #{boardId} 
	 	AND MEMBER_ID 	= #{memberId}


   	 SELECT COUNT(WARING)
	 FROM BOARD_COM_CONFIG
	 WHERE 
	 	BOARD_ID 		= #{boardId} 
	 	AND MEMBER_ID 	= #{memberId}

mapper.delete:

	DELETE 
	FROM BOARD_COM_CONFIG 
	WHERE 
	 	BOARD_ID 		= #{boardId} 
	 	AND MEMBER_ID 	= #{memberId}
	 	AND WARING	IS NULL


	DELETE 
	FROM BOARD_COM_CONFIG 
	WHERE 
	 	BOARD_ID 		= #{boardId} 
	 	AND MEMBER_ID 	= #{memberId}
	 	AND GOOD_OR_NOT	IS NULL
